SELECT detail_pinjam.id_detail_pinjam, inventaris.nama, detail_pinjam.jumlah_peminjaman, peminjam.tanggal_pinjam, peminjam.status_peminjaman, petugas.nama_petugas FROM detail_pinjam JOIN inventaris ON detail_pinjam.id_inventaris = inventaris.id_inventaris JOIN peminjam ON detail_pinjam.id_peminjam = peminjam.id_peminjam JOIN petugas ON inventaris.id_petugas = petugas.id_petugas WHERE peminjam.status_peminjaman = 'dikembalikan';